function burgerMenu(){const navbar=document.querySelector('.navbar');const burger=document.querySelector('.navbar__burger');burger.addEventListener('click',function(){navbar.classList.toggle('show-navbar')})}
function closeBurgerMenu(){const links=document.querySelector('.navbar__links');const navBar=document.querySelector('.navbar');links.addEventListener('click',function(){navBar.classList.remove('show-navbar')})}
const swiper=new Swiper('.swiper',{direction:'horizontal',loop:!0,autoplay:{delay:5000,disableOnInteraction:!1,},pagination:{el:".swiper-pagination",clickable:!0,},navigation:{nextEl:'.swiper-button-next',prevEl:'.swiper-button-prev',},});burgerMenu();closeBurgerMenu()